import logo from './logo.svg';
import './App.css';
import { Vector3, TextureLoader,RepeatWrapping } from "three";
import {useLoader} from "react-three-fiber"
import { StandardEnvironment, Background, Logo } from "spacesvr";


function App() {

  const texture = useLoader(TextureLoader, 'sand.jpg');
  texture.wrapS = texture.wrapT = RepeatWrapping;
  return (
      <StandardEnvironment player={{ pos: new Vector3(5, 1, 0), rot: Math.PI}}>
        <Background color={0xffffff} />
        <Logo floating rotating position={new Vector3(0, 1.25, 0)} />
        <fog attach="fog" args={[0xffffff, 10, 90]} />
        <ambientLight />
        <mesh rotation-x={-Math.PI / 2}>
          <planeBufferGeometry args={[200, 200,200,200]} />

          <meshBasicMaterial map = {texture}/>
        </mesh>
      </StandardEnvironment>
  );
}

export default App;
